import React, { useState } from 'react'

import {
    ComplexInformations,
    SomeQuestions,
    LivingPlaceRegistrationTimeline
 } from '../Components/Registration'

const LivingPlaceRegistration = () => {

    const [data, setData] = useState({
        part_of_complex: '',
        is_existing_complex: '',
        existing_complex_name: '',
        residence_type: '',
        vhe_type: '',
        complex_informations: {
            complex_name: data.complex_name,
            complex_house_number: data.complex_house_number,
            city: data.city,
            postal_code: data.postal_code,
            phone_number: data.phone_number,
            email: data.email,
            street: data.street,
            complex_house_number_extension: data.house_number_extension,
            country: data.country,
            phone_number_landcode: data.phone_number_landcode,
            opening_hours: data.opening_hours,
            complex_facility_types: {
                complex_kitchen: data.kitchen,
                complex_bathroom: data.bathroom,
                complex_supermarket: data.supermarket,
                complex_fitness: data.fitness,
                complex_common_room: data.common_room,
                complex_reception_office: data.reception_office,
                complex_laundry: data.laundry,
                complex_relax: data.relax
            }
        }
    })

    console.log('data', data)

    const makeRequest = (formData) => {
        console.log('form is submitted', formData)
    }

    const handleNextStep = (newData, final = false) => {
        setData((prev) => ({ ...prev, ...newData }))
        if(final) {
            makeRequest(newData)
            return
        }
        setCurrentStep((prev) => prev + 1);
    }

    const handlePreviewStep = (newData) => {
      setData((prev) => ({ ...prev, ...newData }))
      setCurrentStep((prev) => prev - 1);
    }

    const [currentStep, setCurrentStep] = useState(0)

    const steps = [
        <SomeQuestions next={handleNextStep} data={data} />, 
        <ComplexInformations next={handleNextStep} prev={handlePreviewStep} data={data} />
    ]

    return (
        <>
            <LivingPlaceRegistrationTimeline />
            <div>
                {steps[currentStep]}
            </div>
        </>
    )
}

export default LivingPlaceRegistration
