import React, { useState, useEffect } from 'react'

import { Formik, Field, Form, FieldArray } from 'formik'

import PropTypes from 'prop-types'
import { Box, Tabs, Tab, Typography, FormControl, FormLabel, Select, MenuItem, TextField, FormGroup, FormControlLabel, Checkbox } from '@mui/material'

import maximumStayDuration from '../../Json/MaximumStayDuration'
import statusAtPointOfRegestering from '../../Json/StatusAtPointOfRegestering'
import serviceCostTerm from '../../Json/ServiceCostTerm'
import livingPlaceFacilities from '../../Json/LivingPlaceFacilities'

import 'bootstrap/dist/css/bootstrap.css'
import Carousel from 'react-bootstrap/Carousel'

const livingPlace = {
    max_stay_duration: '',
    rental_price_term: '',
    description: '',
    changeover_time: '',
    deposit: '',
    service_costs: '',
    rentalprice: '',
    b2b_price: '',
    b2c_price: '',
    vhe_facility_types: {
        welcome_pack: true,
        bed_sheets: false,
        towels: false,
        bike: false,
        weekly_cleaning: false,
        final_cleaning: false,
        kitchen_supplies: false,
        wifi: false,
        wifi_television: false
    }
}

function TabPanel(props) {

    const { children, value, index, ...other } = props

    return (
        <div
            role='tabpanel'
            hidden={value !== index}
            id={`simple-tabpanel-${index}`}
            aria-labelledby={`simple-tab-${index}`}
            {...other}
        >
            {value === index && (
                <Box sx={{ p: 3 }}>
                    <Typography>{children}</Typography>
                </Box>
            )}
        </div>
    )
}

TabPanel.propTypes = {
    children: PropTypes.node,
    index: PropTypes.number.isRequired,
    value: PropTypes.number.isRequired,
}

function a11yProps(index) {
    return {
        id: `simple-tab-${index}`,
        'aria-controls': `simple-tabpanel-${index}`,
    }
}

const LivingPlaceInformations = ({ name, room }) => (
    <FieldArray
        name={name}
        render={arrayHelpers => (
            <div style={{ marginTop: '8px', backgroundColor: 'rgba(0, 0, 0, 0.1' }}>
                {room.vhes.length ? (
                    room.vhes.map((vhe, index) => (
                        <>
                            <div key={index}>
                                <Field
                                    name={`${name}.${index.max_stay_duration}`}
                                    value={room.vhes[index].max_stay_duration}
                                />
                                <button type='button' onClick={() => arrayHelpers.remove(index)}>remove Color</button>
                            </div>
                            <button type='button' onClick={() => arrayHelpers.insert(index, '')}>Add Color</button>
                            </>
                    ))
                ) : (
                    <button type='button' onClick={() => arrayHelpers.push('')}>Add a color</button>
                )}
            </div>
        )}
    />
)

const LivingPlaceInformationsWrapper = (props) => {

    const [vhesArraysCreated, setVhesArraysCreated] = useState(false)

    let initialValues = {
        rooms: ''
    }

    let vhesArray = []

    let rooms = props.data.rooms

    const [value, setValue] = useState(0)

    const handleChangeTab = (event, newValue) => {
        setValue(newValue, true)
    }

    const numberOfRooms = parseInt(props.data.residence.number_rooms_with_vhes)
    let numberOfLivingPlaces = []
    for(let i = 0; i < numberOfRooms; i++) {
        numberOfLivingPlaces.push(parseInt(props.data.rooms[i].number_of_living_places))
    }

    const setVhesArrays = (rooms, numberOfRooms, numberOfLivingPlaces) => {
        for(let i = 0; i <numberOfRooms; i++) {
            vhesArray = []
            for(let j = 0; j < numberOfLivingPlaces[i]; j++ ){
                vhesArray.push(livingPlace)
            }
            rooms[i].vhes = vhesArray
        }
        return rooms
    }

    useEffect(() => {
        rooms = setVhesArrays(rooms, numberOfRooms, numberOfLivingPlaces)
        initialValues.rooms = props.data.rooms
        setVhesArraysCreated(true)
    })

    if(!vhesArraysCreated) return null

    return (
        <>
            <Formik
                initialValues={{
                    rooms: [
                        {
                            vhes: [
                                {
                                    max_stay_duration: 'test1-1'
                                },
                                {
                                    max_stay_duration: 'test1-2'
                                }
                            ]
                        },
                        {
                            vhes: [
                                {
                                    max_stay_duration: 'test2-1'
                                }
                            ]
                        }
                    ]
                }}
                onSubmit={(values, { setSubmitting }) => {
                    setTimeout(() => {
                    alert(JSON.stringify(values, null, 2))
                    setSubmitting(false)
                    }, 400)
                }}
            >
                {({ values, handleSubmit }) => {
                    return (
                        <form onSubmit={handleSubmit}>
                            <FieldArray
                                name='rooms'
                                render={arrayHelpers => (
                                    <>
                                        {values.rooms && values.rooms.length && (
                                            values.rooms.map((room, index) => (
                                                <div key={index} style={{ marginTop: '24px' }}>
                                                    <LivingPlaceInformations
                                                        name={`rooms.${index}.vhes`}
                                                        room={room}
                                                    />
                                                </div>
                                            ))
                                        )}
                                        <div>
                                            <button type='submit'>Submit</button>
                                        </div>
                                    </>
                                )}
                            />
                        </form>
                    )
                }}
            </Formik>
        </>
    )
}

export default LivingPlaceInformationsWrapper
