import React from 'react'

import { Formik, Field, Form, ErrorMessage, FieldArray } from 'formik'

import axios from 'axios'

import { Button, FormLabel, TextField, FormControl, FormGroup, FormControlLabel, Checkbox, Radio, RadioGroup} from '@mui/material'

const initialValues = {
    floorplan: [
    {
      floor: '',
      area: ''
    }
  ]
}

const FloorplanInformations = (props) => {

    return (
        <>
            <h1>Invite friends</h1>
            <Formik
                initialValues={initialValues}
                onSubmit={(data) => {
                    props.next(data)
                }}
            >
                {({ values }) => (
                    <Form>
                        <FieldArray name='floorplan'>
                            {({ remove, push }) => (
                                <div>
                                    {values.floorplan.length > 0 && values.floorplan.map((room, index) => (
                                        <div className='row' key={index}>

                                            {/* HOUSE NUMBER */}
                                            <FormLabel component='legend' style={{ color: 'black', marginBottom: '10px'}}>House number</FormLabel>
                                            <TextField
                                                name='residence.house_number'
                                                value={formik.values.residence.house_number}
                                                onChange={formik.handleChange}
                                                /* error={getIn(formik.touched, 'residence.house_number') && Boolean(getIn(formik.errors, 'residence.house_number'))}
                                                helperText={getIn(formik.touched, 'residence.house_number') && getIn(formik.errors, 'residence.house_number')} */
                                            />

                                            <div className='col'>
                                                <label htmlFor={`floorplan.${index}.floor`}>Floor</label>
                                                <Field
                                                    name={`floorplan.${index}.floor`}
                                                    type='text'
                                                />
                                                {/* <ErrorMessage
                                                    name={`friends.${index}.name`}
                                                    component='div'
                                                    className='field-error'
                                                /> */}
                                            </div>
                                            <div className='col'>
                                                <label htmlFor={`floorplan.${index}.area`}>Area</label>
                                                <Field
                                                    name={`floorplan.${index}.area`}
                                                    type='text'
                                                />
                                                {/* <ErrorMessage
                                                    name={`friends.${index}.name`}
                                                    component='div'
                                                    className='field-error'
                                                /> */}
                                            </div>
                                            <div className='col'>
                                                <button type='button' className='secondary' onClick={() => remove(index)}>X</button>
                                            </div>
                                        </div>
                                    ))}
                                    <button type='button' className='secondary' onClick={() => push({ floor: '', area: '' })}>Add room</button>
                                </div>
                            )}
                        </FieldArray>
                        <button type='submit'>Submit</button>
                    </Form>
                )}
            </Formik>
        </>
    )
}

export default FloorplanInformations
