import React, { Fragment } from 'react'

import axios from 'axios'

import { Formik, Field, Form, FieldArray } from 'formik'

const FloorplanInformationsFormik = (props) => {

    return (
        <>
            <Formik
                initialValues={{
                    floorplan_informations: []
                }}
                onSubmit={( data ) => {
                    console.log(data)
                    /* const res = axios.post('http://10.0.4.5:8000/api/v1/vhe/registerintroduction/', data)
                    res.then(res => { console.log('res', res) }).catch( error => { console.log(error) }) */
                    props.next(data)
                }}
            >
            {({
                values,
                handleChange,
                handleBlur
            }) => (
                <Form>
                    <FieldArray name={'users'}>
                        {({push, remove}) => {
                            return (
                                <div>
                                    {values.floorplan_informations.map((room, index) => (
                                        <>
                                            <Fragment key={index}>
                                                {/* <TextInput
                                                    name={`users.${index}.name`}
                                                    label={`Name - ${index}`}
                                                /> */}

                                                <p>Room</p>
                                                <Field name={`floorplan_informations.${index}.room`} />

                                                <p>Floor</p>
                                                <Field name={`floorplan_informations.${index}.floor`} />

                                                <p>Area</p>
                                                <Field name={`floorplan_informations.${index}.area`} />
                                            </Fragment>

                                            <button onClick={() => remove(index)}>Remove</button>
                                        </>
                                    ))}

                                    <button onClick={() => push({room: '', floor: '', area: ''})}>Add new room</button>
                                    {/* {errors.users && typeof errors.users === 'string' && <p className={'error'}>{errors.users}</p>} */}
                                </div>
                            )
                        }}
                    </FieldArray>

                    <button type='submit'>Submit</button>
                </Form>
            )}
            </Formik>
        </>
    )
}

export default FloorplanInformationsFormik
