import React, { useState, useEffect } from 'react'

import axios from 'axios'

import { useFormik } from 'formik'
import * as yup from 'yup'

import { Button, Radio, RadioGroup, FormControlLabel, FormControl, FormLabel, MenuItem, Select } from '@mui/material'

import residenceTypes from '../../Json/ResidenceTypes'
import livingPlaceTypes from '../../Json/LivingPlaceTypes'

const validationSchema = yup.object({
    part_of_complex: yup.boolean(),
    is_existing_complex: yup.boolean(),
    introduction: yup.object({
        existing_complex_name: yup.number().required(),
        residence_type: yup.number().required(),
        vhe_type: yup.number().required('A living place type is madatory')
    })
})

const SomeQuestions = (props) => {

    const formik = useFormik({
        initialValues: {
            part_of_complex: '',
            is_existing_complex: '',
            introduction: {
                existing_complex_name: '',
                residence_type: '',
                vhe_type: ''
            }
        },
        /* validationSchema: validationSchema, */
        onSubmit: (data) => {
            console.log(data)
            props.next(data)
        }
    })

    const[complexNames, setComplexNames] = useState([])

    const fetchComplexNames = () => {
        axios.get('http://10.0.4.5:8000/api/v1/vhe/complexname').then(result => {
            const response = result.data.results
            console.log(response)
            setComplexNames(response)
        }).catch(e => {
            console.log('Error getting data: ' + e)
        })
    }

    useEffect(() => {
        fetchComplexNames()
      }, [])

    return (
        <>
            <div>
                <form onSubmit={formik.handleSubmit}>

                    {/* PART OF A COMPLEX */}
                    <FormControl component='fieldset'>
                        <FormLabel component='legend' style={{ color: 'black'}}>Is the residence part of a complex?</FormLabel>
                        <RadioGroup
                            name='part_of_complex'
                            value={formik.values.part_of_complex}
                            onChange={formik.handleChange}
                        >
                            <FormControlLabel
                                value='true'
                                control={<Radio />}
                                label='True'
                            />
                            <FormControlLabel
                                value='false'
                                control={<Radio />}
                                label='False'
                            />
                        </RadioGroup>
                    </FormControl>

                    {/* EXISTING COMPLEX */}
                    {formik.values.part_of_complex === 'true' && (
                        <FormControl component='fieldset'>
                            <FormLabel component='legend' style={{ color: 'black'}}>Is the residence part of a complex?</FormLabel>
                            <RadioGroup
                                name='is_existing_complex'
                                value={formik.values.is_existing_complex}
                                onChange={formik.handleChange}
                            >
                                <FormControlLabel
                                    value='true'
                                    control={<Radio />}
                                    label='True'
                                />
                                <FormControlLabel
                                    value='false'
                                    control={<Radio />}
                                    label='False'
                                />
                            </RadioGroup>
                        </FormControl>
                    )}

                    {/* EXISTING COMPLEX NAME */}
                    {formik.values.part_of_complex === 'true' && formik.values.is_existing_complex === 'true' && (
                        <FormControl>
                            <FormLabel component='legend' style={{ color: 'black', marginBottom: '10px'}}>Choose a complex</FormLabel>
                            <Select
                                name='introduction.existing_complex_name'
                                value={formik.values.introduction.existing_complex_name}
                                onChange={formik.handleChange}
                            >
                                <MenuItem value=''>Choose complex name</MenuItem>
                                {complexNames.map((item, index) => (
                                    <MenuItem key={index} value={item.id}>{item.complex_name}</MenuItem>
                                ))}
                            </Select>
                        </FormControl>
                    )}

                    {/* RESIDENCE TYPE */}
                    <FormControl>
                        <FormLabel component='legend' style={{ color: 'black', marginBottom: '10px'}}>Residence type</FormLabel>
                        <Select
                            name='introduction.residence_type'
                            value={formik.values.introduction.residence_type}
                            onChange={formik.handleChange}
                        >
                            <MenuItem value=''>Choose residence type</MenuItem>
                            {residenceTypes.map((item, index) => (
                                <MenuItem key={index} value={item.value}>{item.label}</MenuItem>
                            ))}
                        </Select>
                    </FormControl>

                    {/* LIVING PLACE TYPE */}
                    <FormControl>
                        <FormLabel component='legend' style={{ color: 'black', marginBottom: '10px'}}>Living place type</FormLabel>
                        <Select
                            name='introduction.vhe_type'
                            value={formik.values.introduction.vhe_type}
                            onChange={formik.handleChange}
                            /* error={formik.touched.introduction.vhe_type && Boolean(formik.errors.introduction.vhe_type)}
                            helperText={formik.touched.vhe_type && formik.errors.vhe_type} */
                        >
                            <MenuItem value=''>Choose living place type</MenuItem>
                            {livingPlaceTypes.map((item, index) => (
                                <MenuItem key={index} value={item.value}>{item.label}</MenuItem>
                            ))}
                        </Select>
                    </FormControl>

                    <Button color='primary' variant='contained' fullWidth type='submit'>
                        Submit
                    </Button>
                </form>
            </div>
        </>
    )
}

export default SomeQuestions
