import React, { useState } from 'react'

import {
    ComplexInformations,
    SomeQuestions,
    LivingPlaceRegistrationTimeline
 } from '../Components/Registration'

const LivingPlaceRegistration = () => {

    const [data, setData] = useState({
        part_of_complex: '',
        is_existing_complex: '',
        introduction: {
            existing_complex_name: '',
            residence_type: '',
            vhe_type: ''
        },
        complex_informations: {
            complex_name: '',
            house_number: '',
            city: '',
            postal_code: '',
            phone_number: '',
            email: '',
            street: '',
            house_number_extension: '',
            country: '',
            phone_number_landcode: '',
            opening_hours: '',
            complex_facility_types: {
                complex_kitchen: '',
                complex_bathroom: '',
                complex_supermarket: '',
                complex_fitness: '',
                complex_common_room: '',
                complex_reception_office: '',
                complex_laundry: '',
                complex_relax: ''
            }
        }
    })

    console.log('data', data)

    const makeRequest = (formData) => {
        console.log('form is submitted', formData)
    }

    const handleNextStep = (newData, final = false) => {
        setData((prev) => ({ ...prev, ...newData }))
        if(final) {
            makeRequest(newData)
            return
        }
        setCurrentStep((prev) => prev + 1);
    }

    const handlePreviewStep = (newData) => {
      setData((prev) => ({ ...prev, ...newData }))
      setCurrentStep((prev) => prev - 1);
    }

    const [currentStep, setCurrentStep] = useState(0)

    const steps = [
        <SomeQuestions next={handleNextStep} data={data} />, 
        <ComplexInformations next={handleNextStep} prev={handlePreviewStep} data={data} />
    ]

    return (
        <>
            <LivingPlaceRegistrationTimeline />
            <div>
                {steps[currentStep]}
            </div>
        </>
    )
}

export default LivingPlaceRegistration
