import React from 'react'

import { Formik, Field, Form, ErrorMessage, FieldArray } from 'formik'

const initialValues = {
    floorplan: [
    {
      floor: '',
      area: ''
    }
  ]
}

const FloorplanInformations = () => (
  <>
    <h1>Invite friends</h1>
    <Formik
        initialValues={initialValues}
        onSubmit={(values) => {
            console.log(values)
        }}
    >
        {({ values }) => (
            <Form>
            <FieldArray name='floorplan'>
                {({ remove, push }) => (
                <div>
                    {values.floorplan.length > 0 && values.floorplan.map((room, index) => (
                        <div className='row' key={index}>
                        <div className='col'>
                            <label htmlFor={`floorplan.${index}.room`}>Room</label>
                            <Field
                                name={`floorplan.${index}.room`}
                                type='text'
                            />
                            {/* <ErrorMessage
                                name={`friends.${index}.name`}
                                component='div'
                                className='field-error'
                            /> */}
                        </div>
                        <div className='col'>
                            <label htmlFor={`floorplan.${index}.area`}>Email</label>
                            <Field
                                name={`floorplan.${index}.area`}
                                type='text'
                            />
                            {/* <ErrorMessage
                                name={`friends.${index}.name`}
                                component='div'
                                className='field-error'
                            /> */}
                        </div>
                        <div className='col'>
                            <button
                            type='button'
                            className='secondary'
                            onClick={() => remove(index)}
                            >
                            X
                            </button>
                        </div>
                        </div>
                    ))}
                    <button
                    type='button'
                    className='secondary'
                    onClick={() => push({ name: '', email: '' })}
                    >
                    Add Friend
                    </button>
                </div>
                )}
            </FieldArray>
            <button type='submit'>Invite</button>
            </Form>
        )}
        </Formik>
    </>
)

export default FloorplanInformations
