import React, { useState } from 'react'

import { Formik, Form, FieldArray } from 'formik'

import PropTypes from 'prop-types'
import { Box, Tabs, Tab, Typography, FormLabel, TextField, FormControl, Select, MenuItem } from '@mui/material'

import livingPlaceTypes from '../../Json/LivingPlaceTypes'

let initialValues = {
    rooms: ''
}

let roomsArray = []

const room = {
    room_type: '',
    room_number: '',
    floor: '',
    dimension_m2: '',
    coliving_allowed: '',
    room_facility_type:  {
        wardrobe: '',
        sink: '',
        working_space: '',
        fridge: '',
        built_in_wardrobe: ''
    }
}

function TabPanel(props) {

    const { children, value, index, ...other } = props

    return (
        <div
            role='tabpanel'
            hidden={value !== index}
            id={`simple-tabpanel-${index}`}
            aria-labelledby={`simple-tab-${index}`}
            {...other}
        >
            {value === index && (
                <Box sx={{ p: 3 }}>
                    <Typography>{children}</Typography>
                </Box>
            )}
        </div>
    )
}

TabPanel.propTypes = {
    children: PropTypes.node,
    index: PropTypes.number.isRequired,
    value: PropTypes.number.isRequired,
}

function a11yProps(index) {
    return {
        id: `simple-tab-${index}`,
        'aria-controls': `simple-tabpanel-${index}`,
    }
}

const RoomsInformations = (props) => {

    const [value, setValue] = useState(0)

    const handleChangeTab = (event, newValue) => {
        setValue(newValue)
    }

    console.log('number_of_rooms_with_living_places', props.data.residence.number_rooms_with_vhes)
    const numberOfRooms = parseInt(props.data.residence.number_rooms_with_vhes)

    for (let i = 0; i < numberOfRooms; i++) {
        roomsArray.push(room)
    }

    console.log('roomsArray', roomsArray)
    initialValues.rooms = roomsArray
    console.log('initialValues.rooms', initialValues.rooms)

    return (
        <>
            <Formik
                initialValues={initialValues}
                onSubmit={(data) => {
                    props.next(data)
                }}
            >
                {({ values, handleChange }) => (
                    <Form>
                        <FieldArray name='rooms'>
                            {({ remove, push }) => (
                                <div>
                                    <Box sx={{ width: '100%' }}>
                                        <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                                            <Tabs value={value} onChange={handleChangeTab} aria-label='basic tabs example' centered>
                                                {Array.from(Array(numberOfRooms), (e, index) => {
                                                    return (
                                                        <Tab label='Item One' {...a11yProps(index)} />
                                                    )
                                                })}
                                            </Tabs>
                                        </Box>
                                        {Array.from(Array(numberOfRooms), (e, index) => {
                                            return (
                                                <TabPanel value={value} index={index}>


                                                {/* LIVING PLACE TYPE */}
                                                <FormControl>
                                                    <FormLabel component='legend' style={{ color: 'black', marginBottom: '10px'}}>Living place type</FormLabel>
                                                    <Select
                                                        name={`rooms.${index}.room_type`}
                                                        value={rooms.[index].room_type}
                                                        onChange={handleChange}
                                                        /* error={formik.touched.introduction.vhe_type && Boolean(formik.errors.introduction.vhe_type)}
                                                        helperText={formik.touched.vhe_type && formik.errors.vhe_type} */
                                                    >
                                                        <MenuItem value=''>Choose a living place type</MenuItem>
                                                        {livingPlaceTypes.map((item, index) => (
                                                            <MenuItem key={index} value={item.value}>{item.label}</MenuItem>
                                                        ))}
                                                    </Select>
                                                </FormControl>
                                                    </TabPanel>
                                                )
                                        })}
                                    </Box>
                                    {props.data.residence.number_rooms_with_vhes > 0 && values.rooms.map((room, index) => (
                                        <div className='row' key={index}>

                                            {/* LIVING PLACE TYPE */}
                                            <FormControl>
                                                <FormLabel component='legend' style={{ color: 'black', marginBottom: '10px'}}>Living place type</FormLabel>
                                                <Select
                                                    name={`rooms.${index}.room_type`}
                                                    /* value={`formik.values.rooms.${index}.room_type`} */
                                                    onChange={handleChange}
                                                    /* error={formik.touched.introduction.vhe_type && Boolean(formik.errors.introduction.vhe_type)}
                                                    helperText={formik.touched.vhe_type && formik.errors.vhe_type} */
                                                >
                                                    <MenuItem value=''>Choose a living place type</MenuItem>
                                                    {livingPlaceTypes.map((item, index) => (
                                                        <MenuItem key={index} value={item.value}>{item.label}</MenuItem>
                                                    ))}
                                                </Select>
                                            </FormControl>

                                            {/* FLOOR */}
                                            <FormLabel component='legend' style={{ color: 'black', marginBottom: '10px'}}>Floor</FormLabel>
                                            <TextField
                                                name={`floorplan.${index}.floor`}
                                                value={room.floor}
                                                onChange={handleChange}
                                                /* error={getIn(formik.touched, 'residence.house_number_extension') && Boolean(getIn(formik.errors, 'residence.house_number_extension'))}
                                                helperText={getIn(formik.touched, 'residence.house_number_extension') && getIn(formik.errors, 'residence.house_number_extension')} */
                                            />

                                            {/* AREA */}
                                            <FormLabel component='legend' style={{ color: 'black', marginBottom: '10px'}}>Area</FormLabel>
                                            <TextField
                                                name={`floorplan.${index}.area`}
                                                value={room.area}
                                                onChange={handleChange}
                                                /* error={getIn(formik.touched, 'residence.house_number_extension') && Boolean(getIn(formik.errors, 'residence.house_number_extension'))}
                                                helperText={getIn(formik.touched, 'residence.house_number_extension') && getIn(formik.errors, 'residence.house_number_extension')} */
                                            />

                                            {/* <div className='col'>
                                                <button type='button' className='secondary' onClick={() => remove(index)}>X</button>
                                            </div> */}
                                        </div>
                                    ))}
                                    {/* <button type='button' className='secondary' onClick={() => push({ room_type: '', room_number: '' })}>Add room</button> */}
                                </div>
                            )}
                        </FieldArray>
                        <button type='submit'>Submit</button>
                    </Form>
                )}
            </Formik>
        </>
    )
}

export default RoomsInformations

