import React from 'react'

import { useForm } from 'react-hook-form'
import { yupResolver } from '@hookform/resolvers/yup'
import * as yup from 'yup'

import { useNavigate } from 'react-router-dom'

import PropTypes from 'prop-types'
import Tabs from '@mui/material/Tabs'
import Tab from '@mui/material/Tab'
import Typography from '@mui/material/Typography'
import Box from '@mui/material/Box'

import { TextField, Button, Select, MenuItem, FormGroup } from '@material-ui/core'
import FormControl from '@material-ui/core/FormControl'
import { Add, Info } from '@material-ui/icons'
import FormControlLabel from '@material-ui/core/FormControlLabel'
import FormLabel from '@material-ui/core/FormLabel'
import Checkbox from '@mui/material/Checkbox'

import { LivingPlaceRegistrationTimeline } from '../Components/Registration/index'

import { useData } from '../Context/DataContext'

import '../Styles/Validations/LivingPlaceRegistrationStep5.scss'




function TabPanel(props) {
    const { children, value, index, ...other } = props

    return (
        <div
            role="tabpanel"
            hidden={value !== index}
            id={`simple-tabpanel-${index}`}
            aria-labelledby={`simple-tab-${index}`}
            {...other}
        >
            {value === index && (
                <Box sx={{ p: 3 }}>
                    <Typography>{children}</Typography>
                </Box>
            )}
        </div>
    )
}

TabPanel.propTypes = {
    children: PropTypes.node,
    index: PropTypes.number.isRequired,
    value: PropTypes.number.isRequired,
}

function a11yProps(index) {
    return {
        id: `simple-tab-${index}`,
        'aria-controls': `simple-tabpanel-${index}`,
    }
}




const LivingPlaceRegistrationStep5 = () => {




    const [value, setValue] = React.useState(0)

    const handleChange = (event, newValue) => {
        setValue(newValue)
    }





    const { setValues, data } = useData()

    const { register, handleSubmit, errors } = useForm({
        defaultValues: {
            rooms_informations: []
        },
        mode: 'onBlur',
        /* resolver: yupResolver(schema) */
    })

    const navigate = useNavigate()

    const onSubmit = (data) => {
        navigate('/living_place_registration/step6')
        setValues(data)
        console.log(data)
    }

    const previewStep = (data) => {
        navigate('/living_place_registration/step4')
        setValues(data)
        console.log(data)
    }

    return (
        <>
            <form
                onSubmit={handleSubmit(onSubmit)}
                className='timelineFrom__container'
            >




            <Box sx={{ width: '100%' }}>
            <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                <Tabs value={value} onChange={handleChange} aria-label="basic tabs example">
                <Tab label="Item One" {...a11yProps(0)} />
                <Tab label="Item Two" {...a11yProps(1)} />
                <Tab label="Item Three" {...a11yProps(2)} />
                </Tabs>
            </Box>
            <TabPanel value={value} index={0}>
                Item One
            </TabPanel>
            <TabPanel value={value} index={1}>
                Item Two
            </TabPanel>
            <TabPanel value={value} index={2}>
                Item Three
            </TabPanel>
            </Box>





                <LivingPlaceRegistrationTimeline />
                <h5>Rooms informations</h5>

                <div id='roomsTab__div'>
                    {/* {values.residence_informations.number_of_rooms.forEach((index) => { */}
                    {/* })} */}
                </div>

                <div id='roomsInformationsPanels__div'>

                    <div id='roomsInformationsLeftPanel__div'>

                        {/* ROOM TYPE */}
                        <FormControl>
                            <div id='roomType__div'>
                            <FormLabel component='legend' style={{ color: 'black', marginBottom: '10px'}}>Room type</FormLabel>
                                <Select
                                    {...register('room_type')}
                                    style={textFieldStyle}
                                >
                                    {roomTypes__array.map((item, index) => (
                                        <MenuItem key={index} value={item.value}>{item.label}</MenuItem>
                                    ))}
                                </Select>
                            </div>
                        </FormControl>

                        {/* FLOOR */}
                        <FormControl>
                            <div id='floor__div'>
                                <FormLabel component='legend' style={{ color: 'black', marginBottom: '10px'}}>Floor</FormLabel>
                                <TextField
                                    {...register('floor')}
                                    placeholder='Floor'
                                    style={textFieldStyle}
                                    InputProps={{ 
                                        disableUnderline: true
                                    }}
                                />
                            </div>
                        </FormControl>
                    </div>

                    <div id='roomsInformationsRightPanel__div'>

                        {/* HOUSE NUMBER EXTENSION OF THE ROOM */}
                        <FormControl>
                            <div id='houseNumberExtensionOfTheRoom__div'>
                                <FormLabel component='legend' style={{ color: 'black', marginBottom: '10px'}}>House number extension of the room</FormLabel>
                                <TextField
                                    {...register('floor')}
                                    placeholder='Room number extension'
                                    style={textFieldStyle}
                                    InputProps={{ 
                                        disableUnderline: true
                                    }}
                                />
                            </div>
                        </FormControl>

                        {/* DIMENSION IN M2 */}
                        <FormControl>
                            <div id='dimensionInSquareMeters__div'>
                                <FormLabel component='legend' style={{ color: 'black', marginBottom: '10px'}}>Dimension in m2</FormLabel>
                                <TextField
                                    {...register('dimension_in_squre_meters')}
                                    placeholder='Dimension'
                                    style={textFieldStyle}
                                    InputProps={{ 
                                        disableUnderline: true
                                    }}
                                />
                            </div>
                        </FormControl>
                    </div>
                </div>

                {/* ROOMS FACILITY TYPES */}
                <FormGroup>
                    <div id='roomsFacilities__div'>
                    <FormLabel component='legend' style={{ color: 'black', marginBottom: '10px'}}>Facility types</FormLabel>
                        <div id='roomsFacilitiesTypes__div'>
                            <FormGroup>
                                {facilityTypes__array.map((item, index) => (
                                    <FormControlLabel key={index} {...register('residence_facility_types.' + item.value)} control={<Checkbox />}label={item.label} />
                                ))}
                            </FormGroup>
                        </div>
                    </div>
                </FormGroup>

                <div id='roomsInformationsButtons__div'>
                    {/* PREVIEW */}
                    <Button
                        type='submit'
                        variant='contained'
                        style={{
                            backgroundColor: '#5398D4',
                            borderRadius: 50,
                            fontFamily: 'DMSansMedium',
                            boxShadow: 'unset'
                        }}
                        onClick={previewStep}
                    >
                        Preview
                    </Button>

                    {/* NEXT */}
                    <div id='roomsInformationsNextButton__div'>
                        <Info style={{ color: '#eeeeee' }}/>
                        <Button
                            type='submit'
                            variant='contained'
                            style={{
                                backgroundColor: '#5398D4',
                                borderRadius: 50,
                                fontFamily: 'DMSansMedium',
                                boxShadow: 'unset',
                                marginLeft: '10px'
                            }}
                        >
                            Next
                        </Button>
                    </div>
                </div>
            </form>
        </>
    )
}

const roomTypes__array = [
    {
        value: 'single_room',
        label: 'Single room'
    },
    {
        value: 'double_room',
        label: 'Double room'
    },
    {
        value: 'twin_room',
        label: 'Twin room'
    }
]

const facilityTypes__array = [
    {
        value: 'wardrobe',
        label: 'Wardrobe'
    },
    {
        value: 'fridge',
        label: 'Fridge'
    },
    {
        value: 'sink',
        label: 'Sink'
    },
    {
        value: 'builtIn_wardrobe',
        label: 'Built-in wardrobe'
    },
    {
        value: 'working_space',
        label: 'Working space'
    }
]

const textFieldStyle = {
    width: '300px',
    height: '40px',
    borderRadius: '5px',
    background: '#eee',
    paddingLeft: '15px',
    paddingTop: '5px'
}

export default LivingPlaceRegistrationStep5
