import React, { useState, useEffect } from 'react'

import { Formik, Form, FieldArray } from 'formik'

import PropTypes from 'prop-types'
import { Box, Tabs, Tab, Typography, FormControl, FormLabel, Select, MenuItem, TextField, FormGroup, FormControlLabel, Checkbox } from '@mui/material'

import maximumStayDuration from '../../Json/MaximumStayDuration'
import statusAtPointOfRegestering from '../../Json/StatusAtPointOfRegestering'
import serviceCostTerm from '../../Json/ServiceCostTerm'
import livingPlaceFacilities from '../../Json/LivingPlaceFacilities'

import 'bootstrap/dist/css/bootstrap.css'
import Carousel from 'react-bootstrap/Carousel'

let initialValues = {
    vhes: ''
}

let vhesArray = []

const livingPlace = {
    max_stay_duration: '',
    rental_price_term: '',
    description: '',
    changeover_time: '',
    deposit: '',
    service_costs: '',
    rentalprice: '',
    b2b_price: '',
    b2c_price: '',
    vhe_facility_types: {
        welcome_pack: '',
        bed_sheets: '',
        towels: '',
        bike: '',
        weekly_cleaning: '',
        final_cleaning: '',
        kitchen_supplies: '',
        wifi: '',
        wifi_television: ''
    }
}

function TabPanel(props) {

    const { children, value, index, ...other } = props

    return (
        <div
            role='tabpanel'
            hidden={value !== index}
            id={`simple-tabpanel-${index}`}
            aria-labelledby={`simple-tab-${index}`}
            {...other}
        >
            {value === index && (
                <Box sx={{ p: 3 }}>
                    <Typography>{children}</Typography>
                </Box>
            )}
        </div>
    )
}

TabPanel.propTypes = {
    children: PropTypes.node,
    index: PropTypes.number.isRequired,
    value: PropTypes.number.isRequired,
}

function a11yProps(index) {
    return {
        id: `simple-tab-${index}`,
        'aria-controls': `simple-tabpanel-${index}`,
    }
}

