import React, { useState, useEffect } from 'react'

import { Formik, Form, FieldArray, Field } from 'formik'

import PropTypes from 'prop-types'
import { Box, Tabs, Tab, Typography, FormControl, FormLabel, Select, MenuItem, TextField, FormGroup, FormControlLabel, Checkbox } from '@mui/material'

import maximumStayDuration from '../../Json/MaximumStayDuration'
import statusAtPointOfRegestering from '../../Json/StatusAtPointOfRegestering'
import serviceCostTerm from '../../Json/ServiceCostTerm'
import livingPlaceFacilities from '../../Json/LivingPlaceFacilities'

import 'bootstrap/dist/css/bootstrap.css'
import Carousel from 'react-bootstrap/Carousel'

const livingPlace = {
    max_stay_duration: '',
    rental_price_term: '',
    description: '',
    changeover_time: '',
    deposit: '',
    service_costs: '',
    rentalprice: '',
    b2b_price: '',
    b2c_price: '',
    vhe_facility_types: {
        welcome_pack: '',
        bed_sheets: '',
        towels: '',
        bike: '',
        weekly_cleaning: '',
        final_cleaning: '',
        kitchen_supplies: '',
        wifi: '',
        wifi_television: ''
    }
}

function TabPanel(props) {

    const { children, value, index, ...other } = props

    return (
        <div
            role='tabpanel'
            hidden={value !== index}
            id={`simple-tabpanel-${index}`}
            aria-labelledby={`simple-tab-${index}`}
            {...other}
        >
            {value === index && (
                <Box sx={{ p: 3 }}>
                    <Typography>{children}</Typography>
                </Box>
            )}
        </div>
    )
}

TabPanel.propTypes = {
    children: PropTypes.node,
    index: PropTypes.number.isRequired,
    value: PropTypes.number.isRequired,
}

function a11yProps(index) {
    return {
        id: `simple-tab-${index}`,
        'aria-controls': `simple-tabpanel-${index}`,
    }
}

const LivingPlaceInformations = ({ name, room }) => (
    <FieldArray
        name={name}
        render={arrayHelpers => (
            <div style={{ marginTop: '8px', backgroundColor: 'rgba(0, 0, 0, 0.1' }}>
                {room.vhes.map((vhe, index) => {
                    <>
                        <Field
                            name={`${name}.${index}.max_stay_duration`}
                            value={room.vhes[index].max_stay_duration}
                        />
                        {/* <ErrorMessage name={`${name}.${index}.max_stay_duration`} /> */}
                        <button type='button' onClick={() => arrayHelpers.remove(index)}>remove Vhe</button>
                        <button type='button' onClick={() => arrayHelpers.insert(index, '')}>Add Vhe</button>
                    </>
                })}
            </div>
        )}
    />
)

const LivingPlaceInformationsWrapper = (props) => {

    const [vhesArraysCreated, setVhesArraysCreated] = useState(false)

    let initialValues = {
        rooms: ''
    }

    let vhesArray = []

    let rooms = props.data.rooms

    const [value, setValue] = useState(0)

    const handleChangeTab = (event, newValue) => {
        setValue(newValue, true)
    }

    const numberOfRooms = parseInt(props.data.residence.number_rooms_with_vhes)
    let numberOfLivingPlaces = []
    for(let i = 0; i < numberOfRooms; i++) {
        numberOfLivingPlaces.push(parseInt(props.data.rooms[i].number_of_living_places))
    }

    const setVhesArrays = () => {
        for(let i = 0; i <numberOfRooms; i++) {
            vhesArray = []
            for(let j = 0; j < numberOfLivingPlaces[i]; j++ ){
                vhesArray.push(livingPlace)
            }
            rooms[i].vhes = vhesArray
        }
        return rooms
    }

    useEffect(() => {
        setVhesArrays()
        initialValues.rooms = rooms
        console.log(initialValues)
        setVhesArraysCreated(true)
    }, [numberOfRooms])

    if(!vhesArraysCreated) return null

    return (
        <>
            <Formik
                initialValues={initialValues}
                onSubmit={(values, { setSubmitting }) => {
                    setTimeout(() => {
                        alert(JSON.stringify(values, null, 2))
                            setSubmitting(false)
                            console.log(values)
                    }, 400)
                }}
            >
                {({ values, handleChange }) => (
                    <Form>
                        <FieldArray name='rooms'>
                            {({ remove, push }) => (
                                <div>
                                    <Box sx={{ width: '100%' }}>
                                        <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                                            <Tabs value={value} onChange={handleChangeTab} aria-label='basic tabs example' centered>
                                                {Array.from(Array(numberOfRooms), (e, index) => {
                                                    return (
                                                        <Tab label='Item One' {...a11yProps(index)} />
                                                    )
                                                })}
                                            </Tabs>
                                        </Box>
                                        {props.data.residence.number_rooms_with_vhes > 0 && rooms.map((room, index) => (
                                            <TabPanel value={value} index={index}>
                                               {/*  <Carousel style={{ backgroundColor: 'red' }} interval={20} wrap={false}> */}
                                                    <div key={index} style={{ marginTop: '24px' }}>
                                                        <LivingPlaceInformations
                                                            name={`rooms.${index}.vhes`}
                                                            room={room}
                                                        />
                                                    </div>
                                               {/*  </Carousel> */}
                                            </TabPanel>
                                        ))}
                                    </Box>
                                </div>
                            )}
                        </FieldArray>
                        <button type='submit'>Submit</button>
                    </Form>
                )}
            </Formik>
        </>
    )
}

export default LivingPlaceInformationsWrapper
