import React, { useState, useEffect } from 'react'

import { Formik, Form, FieldArray } from 'formik'

import PropTypes from 'prop-types'
import { Box, Tabs, Tab, Typography, FormControl, FormLabel, Select, MenuItem, TextField, FormGroup, FormControlLabel, Checkbox } from '@mui/material'

import maximumStayDuration from '../../Json/MaximumStayDuration'
import statusAtPointOfRegestering from '../../Json/StatusAtPointOfRegestering'
import serviceCostTerm from '../../Json/ServiceCostTerm'
import livingPlaceFacilities from '../../Json/LivingPlaceFacilities'

import 'bootstrap/dist/css/bootstrap.css'
import Carousel from 'react-bootstrap/Carousel'

const livingPlace = {
    max_stay_duration: '',
    rental_price_term: '',
    description: '',
    changeover_time: '',
    deposit: '',
    service_costs: '',
    rentalprice: '',
    b2b_price: '',
    b2c_price: '',
    vhe_facility_types: {
        welcome_pack: true,
        bed_sheets: false,
        towels: false,
        bike: false,
        weekly_cleaning: false,
        final_cleaning: false,
        kitchen_supplies: false,
        wifi: false,
        wifi_television: false
    }
}

function TabPanel(props) {

    const { children, value, index, ...other } = props

    return (
        <div
            role='tabpanel'
            hidden={value !== index}
            id={`simple-tabpanel-${index}`}
            aria-labelledby={`simple-tab-${index}`}
            {...other}
        >
            {value === index && (
                <Box sx={{ p: 3 }}>
                    <Typography>{children}</Typography>
                </Box>
            )}
        </div>
    )
}

TabPanel.propTypes = {
    children: PropTypes.node,
    index: PropTypes.number.isRequired,
    value: PropTypes.number.isRequired,
}

function a11yProps(index) {
    return {
        id: `simple-tab-${index}`,
        'aria-controls': `simple-tabpanel-${index}`,
    }
}

const LivingPlaceInformations = ({ indexVhe, name, room, handleChange }) => (
    <FieldArray
        name={name}
        render={arrayHelpers => (
            <FormControl style={{ marginLeft: '150px',  marginTop: '150px',  marginBottom: '150px' }}>
                <FormLabel component='legend' style={{ color: 'black', marginBottom: '10px'}}>Maximum stay duration</FormLabel>
                <Select
                    /* name={`rooms.${index}.vhes.${indexVhe}.max_stay_duration`} */
                    name={`${name}.${indexVhe}.max_stay_duration`}
                    /* value={'short_stay'} */
                    onChange={handleChange}
                    /* error={formik.touched.introduction.vhe_type && Boolean(formik.errors.introduction.vhe_type)}
                    helperText={formik.touched.vhe_type && formik.errors.vhe_type} */
                >
                    {maximumStayDuration.map((item, index) => (
                        <MenuItem key={index} value={item.value}>{item.label}</MenuItem>
                    ))}
                </Select>
            </FormControl>
        )}
    />
)

const LivingPlaceInformationsWrapper = (props) => {

    const [vhesArraysCreated, setVhesArraysCreated] = useState(false)

    let initialValues = {
        rooms: ''
    }

    let vhesArray = []

    let rooms = props.data.rooms

    const [value, setValue] = useState(0)

    const handleChangeTab = (event, newValue) => {
        setValue(newValue, true)
    }

    const numberOfRooms = parseInt(props.data.residence.number_rooms_with_vhes)
    let numberOfLivingPlaces = []
    for(let i = 0; i < numberOfRooms; i++) {
        numberOfLivingPlaces.push(parseInt(props.data.rooms[i].number_of_living_places))
    }

    const setVhesArrays = (rooms, numberOfRooms, numberOfLivingPlaces) => {
        for(let i = 0; i <numberOfRooms; i++) {
            vhesArray = []
            for(let j = 0; j < numberOfLivingPlaces[i]; j++ ){
                vhesArray.push(livingPlace)
            }
            rooms[i].vhes = vhesArray
        }
        return rooms
    }

    useEffect(() => {
        rooms = setVhesArrays(rooms, numberOfRooms, numberOfLivingPlaces)
        initialValues.rooms = props.data.rooms
        setVhesArraysCreated(true)
    })

    if(!vhesArraysCreated) return null

    return (
        <>
            <Formik
                initialValues={initialValues}
                onSubmit={(data) => {
                    console.log(data)
                    props.next(data)
                }}
            >
                {({ values, handleChange }) => (
                    <Form>
                        <FieldArray name='rooms'>
                            {({ remove, push }) => (
                                <div>
                                    <Box sx={{ width: '100%' }}>
                                        <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                                            <Tabs value={value} onChange={handleChangeTab} aria-label='basic tabs example' centered>
                                                {Array.from(Array(numberOfRooms), (e, index) => {
                                                    return (
                                                        <Tab label='Item One' {...a11yProps(index)} />
                                                    )
                                                })}
                                            </Tabs>
                                        </Box>
                                        {props.data.residence.number_rooms_with_vhes > 0 && rooms.map((room, index) => (
                                            <TabPanel value={value} index={index}>
                                                <Carousel style={{ backgroundColor: 'red' }} interval={20} wrap={false}>
                                                    {Array.from(Array(numberOfLivingPlaces[index]), (e, indexVhe) => {
                                                        return (
                                                            <Carousel.Item>
                                                                <LivingPlaceInformations
                                                                    index={indexVhe}
                                                                    name={`rooms.${index}.vhes`}
                                                                    room={room}
                                                                    handleChange={handleChange}
                                                                />
                                                            </Carousel.Item>
                                                        )
                                                    })}
                                                </Carousel>
                                            </TabPanel>
                                        ))}
                                    </Box>
                                </div>
                            )}
                        </FieldArray>
                        <button type='submit'>Submit</button>
                    </Form>
                )}
            </Formik>
        </>
    )
}

export default LivingPlaceInformationsWrapper
