import React from 'react'

import { Formik, Form, FieldArray } from 'formik'

import { FormLabel, TextField } from '@mui/material'

const initialValues = {
    rooms: [
        {
            room_type: '',
            room_number: '',
            floor: '',
            dimension_m2: '',
            coliving_allowed: '',
            room_facility_type:  {
                wardrobe: '',
                sink: '',
                working_space: '',
                fridge: '',
                built_in_wardrobe: ''
            }
        }
    ]
}

const RoomsInformations = (props) => {

    console.log(props.data.residence.number_rooms_with_vhes)
    const numberOfRooms= props.data.residence.number_rooms_with_vhes

    return (
        <>
            <Formik
                initialValues={initialValues}
                onSubmit={(data) => {
                    props.next(data)
                }}
            >
                {({ values, handleChange }) => (
                    <Form>
                        <FieldArray name='rooms'>
                            {({ remove, push }) => (
                                <div>
                                    {/* for(i = 0, i < {props.data.residence.number_rooms_with_vhes}, i++) */}
                                    {props.data.residence.number_rooms_with_vhes > 0 && values.rooms.map((room, index) => (
                                        <div className='row' key={index}>

                                            {/* FLOOR */}
                                            <FormLabel component='legend' style={{ color: 'black', marginBottom: '10px'}}>Floor</FormLabel>
                                            <TextField
                                                name={`floorplan.${index}.floor`}
                                                value={room.floor}
                                                onChange={handleChange}
                                                /* error={getIn(formik.touched, 'residence.house_number_extension') && Boolean(getIn(formik.errors, 'residence.house_number_extension'))}
                                                helperText={getIn(formik.touched, 'residence.house_number_extension') && getIn(formik.errors, 'residence.house_number_extension')} */
                                            />

                                            {/* AREA */}
                                            <FormLabel component='legend' style={{ color: 'black', marginBottom: '10px'}}>Area</FormLabel>
                                            <TextField
                                                name={`floorplan.${index}.area`}
                                                value={room.area}
                                                onChange={handleChange}
                                                /* error={getIn(formik.touched, 'residence.house_number_extension') && Boolean(getIn(formik.errors, 'residence.house_number_extension'))}
                                                helperText={getIn(formik.touched, 'residence.house_number_extension') && getIn(formik.errors, 'residence.house_number_extension')} */
                                            />

                                            {/* <div className='col'>
                                                <button type='button' className='secondary' onClick={() => remove(index)}>X</button>
                                            </div> */}
                                        </div>
                                    ))}
                                    {/* <button type='button' className='secondary' onClick={() => push({ room_type: '', room_number: '' })}>Add room</button> */}
                                </div>
                            )}
                        </FieldArray>
                        <button type='submit'>Submit</button>
                    </Form>
                )}
            </Formik>
        </>
    )
}

export default RoomsInformations

