import React from 'react'

import {
    Formik,
    Form,
    FieldArray,
    getIn
} from 'formik'
import * as yup from 'yup'

import {
    FormLabel,
    TextField
} from '@mui/material'

const initialValues = {
    floorplan: [{
        floor: '',
        area: ''
    }]
}

const FloorplanInformations = (props) => {

    return ( <
        >
        <
        Formik initialValues = {
            initialValues
        }
        onSubmit = {
            (data) => {
                props.next(data)
            }
        } >
        {
            ({
                values,
                handleChange
            }) => ( <
                Form >
                <
                FieldArray name = 'floorplan' > {
                    ({
                        remove,
                        push
                    }) => ( <
                        div > {
                            values.floorplan.length > 0 && values.floorplan.map((room, index) => ( <
                                div className = 'row'
                                key = {
                                    index
                                } >

                                {
                                    /* FLOOR */ } <
                                FormLabel component = 'legend'
                                style = {
                                    {
                                        color: 'black',
                                        marginBottom: '10px'
                                    }
                                } > Floor < /FormLabel> <
                                TextField name = {
                                    `floorplan.${index}.floor`
                                }
                                value = {
                                    room.floor
                                }
                                onChange = {
                                    handleChange
                                }
                                error = {
                                    getIn(formik.touched, 'residence.house_number_extension') && Boolean(getIn(formik.errors, 'residence.house_number_extension'))
                                }
                                helperText = {
                                    getIn(formik.touched, 'residence.house_number_extension') && getIn(formik.errors, 'residence.house_number_extension')
                                }
                                />

                                {
                                    /* AREA */ } <
                                FormLabel component = 'legend'
                                style = {
                                    {
                                        color: 'black',
                                        marginBottom: '10px'
                                    }
                                } > Area < /FormLabel> <
                                TextField name = {
                                    `floorplan.${index}.area`
                                }
                                value = {
                                    room.area
                                }
                                onChange = {
                                    handleChange
                                }
                                /* error={getIn(formik.touched, 'residence.house_number_extension') && Boolean(getIn(formik.errors, 'residence.house_number_extension'))}
                                helperText={getIn(formik.touched, 'residence.house_number_extension') && getIn(formik.errors, 'residence.house_number_extension')} */
                                />

                                <
                                div className = 'col' >
                                <
                                button type = 'button'
                                className = 'secondary'
                                onClick = {
                                    () => remove(index)
                                } > X < /button> <
                                /div> <
                                /div>
                            ))
                        } <
                        button type = 'button'
                        className = 'secondary'
                        onClick = {
                            () => push({
                                floor: '',
                                area: ''
                            })
                        } > Add room < /button> <
                        /div>
                    )
                } <
                /FieldArray> <
                button type = 'submit' > Submit < /button> <
                /Form>
            )
        } <
        /Formik> <
        />
    )
}

export default FloorplanInformations

